import react from "react";
import{View,Text,TextInput,StyleSheet,Platform} from 'react-native'

const CustomInput=({value,setValue,placeholder,secureTextEntry})=>{
    const [hidepassword,setHidePassword]=useState(value);
    return(
        <View style={styles.container}>
            <TextInput
            placeholder={placeholder} 
            style={styles.input}
            value={value}
            onChangeText={setValue}
            secureTextEntry={secureTextEntry}
            />
            <Icon onPress={()=>setHidePassword(!hidepassword)}
            style={{size:22,color:black}} name='eye-outline'/>
        </View>
    )
}
const styles=StyleSheet.create({
    container:{
        width:'75%',
    },
    input:{
        fontSize:14,
        fontWeight:'500',
        lineHeight: Platform.OS === 'ios' ? 20 : 40,
        height:Platform.OS === 'ios' ? 25 : 42,
        paddingHorizontal:5,
        borderBottomWidth:1.8,
        borderBottomColor:'#79B5AF'
    },
})
export default CustomInput
