import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView} from 'react-native';
import {LangPicker} from './components/LangPicker';
import CustomInput from './components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from './components/CustomButton';

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function SignUp(navigation) {

    const[username,setUsername]=useState('');
    const[password,setPassword]=useState('');
  
    const [chooseData,setchooseData]=useState('En');
    const [isLangVisible,setisLangVisible]= useState(false);
  
    const changeLangVisibility=(bool)=>{
      setisLangVisible(bool)
    }
    const setData=(option)=>{
      setchooseData(option)
    }
  
    const onSignIn=()=>{
      console.warn('Sign In');
    };
    const onSignUp=()=>{
        console.warn('Sign Up');
      };
  
    return (
        <NavigationContainer>
        <Stack.Navigator>
        <Stack.Screen
          name="SignUp"
          component={SignUp}
        />
        <Stack.Screen name="Profile" component={ProfileScreen} />
        </Stack.Navigator>
        <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>
        <StatusBar style="auto" />
        <ScrollView>
        <KeyboardAvoidingView behavior='' style={{flex:1}}>
        <Text style={styles.sectionTitle}>Sign In</Text>
        <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={[styles.text,{}]}>{chooseData}</Text><Icon name="chevron-down" size={19} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
        </KeyboardAvoidingView>
        </ScrollView>
        </SafeAreaView>
        </NavigationContainer>
    );
}

const styles = StyleSheet.create({
    container: {
      resizeMode: 'contain',
      flex:1,
      ...Platform.select({
        ios:{
        },
        android:{
        },
        default:{
          marginTop:HEIGHT/10,
          width:WIDTH/3+50,
          alignSelf:'center',
        },
      })
    },
    inputContainer:{
      flex:1,
      justifyContent:'center',
      alignItems:'center',
      ...Platform.select({
        ios:{
          marginTop:'20%',
        },
        android:{
          marginTop:'15%',
        },
        default:{
        },
      })
    },
    sectionTitle: {
      height: 50,
      fontSize: 34,
      fontWeight: 'bold',
      fontFamily:'sans-serif',
      ...Platform.select({
      ios: {
        alignSelf:'flex-start',
        marginLeft: 40,
        marginTop: 80,
      },
      android: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
      },
      default: {
        alignSelf:'center',
      },
    })
    },
    text:{
      fontSize:14,
      fontWeight:'500',
      ...Platform.select({
        ios: {
        fontSize:18,
        color:'grey',
        },
        android: {
        fontSize:18,
        color:'grey',
        },
        default: {
          color:'black',
          marginLeft:10,
        },
      })
    },
    square:{
      width: 70,
      height: 40,
      flexDirection:'row',
      alignItems:'center',
      marginTop:20,
      right:'8%',
      ...Platform.select({
        ios: {
          backgroundColor:'#edf1f2',
          opacity:0.5,
              },
        android: {
          backgroundColor:'#edf1f2',
          opacity:0.5,
              },
        default: {
          marginTop:80  ,
          backgroundColor:'white',
          borderColor: "#e2e1df",
          borderWidth:1.6,
          opacity:0.9
        },
      })
      },
    touchableOpacity:{
      ...Platform.select({
        ios: {
          position:'absolute',
          alignSelf:'flex-end',
          top:'2%',
          right:'2%',
              },
        android: {
          position:'absolute',
          alignSelf:'flex-end',
          top:'2%',
          right:'2%',
              },
        default: {
          alignSelf:'flex-end',
        },
      })
      },
      whiteb:{
        backgroundColor: 'white',
        ...Platform.select({
          ios: {
            marginTop:100,
            flex:1,
            borderRadius:15,
            paddingBottom:'85%'
                },
          android: {
            marginTop:'20%',
            flex:1,
            borderRadius:15,
            paddingBottom:'70%',
                },
          default: {
            marginTop:20,
            flex:1,
            paddingVertical:55,
            borderColor: "#e2e1df",
            borderWidth:1,
          },
        }) 
      },
  });
