import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Touchable, TouchableOpacity, View,Modal , Platform} from 'react-native';
import {LangPicker} from './components/LangPicker';
import {Icon } from 'react-native-vector-icons'
export default function App() {


  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }
  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={styles.text}>{chooseData}</Text><Icon name='Down'></Icon>
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#edf1f2',
    resizeMode: 'contain', 
    flex:1
  },
  sectionTitle: {
    height: 50,
    fontSize: 40,
    fontWeight: 'bold',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 40,
    marginTop: 80,
    },
    default: {
      alignSelf:'center',
      marginTop:100
    },
  })
  },
  text:{
    fontSize:22,
    ...Platform.select({
      ios: {
      color:'grey',
      },
      android: {
      color:'grey',
      },
      default: {
        color:'black',

      },
    })
  },
  square:{
    width: 40,
    height: 40,

    ...Platform.select({
      ios: {
        backgroundColor:'#edf1f2'
            },
      android: {
        backgroundColor:'#edf1f2'
            },
      default: {
        backgroundColor:'white',
        borderColor: "#e2e1df",
        borderWidth: 1.6,
        opacity:0.9
      },
    })
    },
  touchableOpacity:{
    ...Platform.select({
      ios: {
        alignSelf:'flex-end',
        width:50        
      },
      android: {
        alignSelf:'flex-end',        
            },
      default: {
        alignSelf:'flex-end',
        marginRight:400,
      },
    })
    },
});
