import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView, FlatList, ViewBase, ActivityIndicator} from 'react-native';
import {LangPicker} from '../components/LangPicker';
import CustomInput from '../components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from '../components/CustomButton';
import SignIn from './SignIn';
import react from 'react';
import axios from 'axios';
import { API } from '../config';
import { BorderTopOutlined } from '@ant-design/icons';
import moment from "moment"

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function WebHome() {
    const [data,setData]=useState([]);
    const [isLoading,setLoading]=useState(false);
    useEffect(()=>{
        setLoading(true);
        axios.get(`${API}/employees`,{headers:{"Access-Control-Allow-Origin":true}})
        .then(({data})=>setData(data))
        .catch(console.error)
        .finally(()=>setLoading(false));
    },[])

    const renderEmployee=({item})=>{
        var  months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        var d = new Date(item.datejoined);
        var monthName=months[d.getMonth()]; // "July" (or current month)
        var dayName=d.getDay();
        var yearName=d.getFullYear();
        return(
            <View style={styles.item}>
                <Image style={styles.image}
                source={{uri:'https://picsum.photos/150'}}
                resizeMode='contain'
                />
                <View style={styles.wrapText}>
                <Text styles={styles.font}>{`${item.fname}                                         ${item.lname}                                       ${item.phone}                      ${item.address}                                ${item.roll}                                         ${dayName} ${monthName} ${yearName}`}</Text>
                </View>
            </View>
        )
    }
    return(
    <SafeAreaView style={{backgroundColor:'#edf1f2'}}>
      <StatusBar style="auto" />
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Managing Employees</Text>
      <View style={{left:0,bottom:30,alignSelf:'flex-end'}}>
      <CustomButton text={'+ Add Employee'}></CustomButton>
      </View>
      <View style={styles.whiteb}>
      <View style={{flexDirection:'row',marginLeft:45,marginTop:20,justifyContent:'space-around',width:1100}}>
         <Text style={styles.tabletitle}>First Name</Text>
          <Text style={styles.tabletitle}>Last Name</Text>
          <Text style={styles.tabletitle}>Phone</Text>
          <Text style={styles.tabletitle}>Address</Text>
          <Text style={styles.tabletitle}>                Roll          </Text>
          <Text style={styles.tabletitle}>Start Date</Text>
          </View>
          {
              isLoading?<ActivityIndicator/>:(
      <FlatList data={data}
      keyExtractor={item=>`key-${item.id}`}
      renderItem={renderEmployee}/>
      )
     }
      </View>
      </View>
    </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        alignSelf:'center',
        width:1300,
    },
    sectionTitle:{
        alignSelf:'flex-start',
        marginTop:'5%',
        fontSize: 30,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    whiteb:{
        marginTop:50,
        backgroundColor:'white',
        height:450,
        width:'100%',
    },
    tabletitle:{
        color:'lightslategrey',
        fontSize:13,
        marginBottom:15,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    item:{
        flexDirection:'row',
        marginBottom:5,
        borderBottomColor:'#edf1f2',
        borderTopColor:'#edf1f2',
        borderTopWidth:1.5,
        borderBottomWidth:1.5,
        width:'100%',
        height:100,
        alignItems:'center'
    },
    image:{
        width:70,
        height:70,
        borderRadius:80,
        marginLeft:20,
    },
    wrapText:{
        flex:1,
        marginLeft:15,
        justifyContent:'center'
    },
    font:{
        fontSize:10,
        fontWeight:'800',
    },
})
