import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView, FlatList, ViewBase, ActivityIndicator} from 'react-native';
import CustomButton from '../components/CustomButton';
import react from 'react';
import axios from 'axios';
import { API } from '../config';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function WebHome() {
    const [data,setData]=useState([]);
    const [isLoading,setLoading]=useState(false);
    const [isEditing,setEditing]=useState(true);

    useEffect(()=>{
        setLoading(true);
        axios.get(`${API}/employees`,{headers:{"Access-Control-Allow-Origin":true,}})
        .then(({data})=>setData(data))
        .catch(console.log())
        .finally(()=>setLoading(false));
    },[])
    
    const onAddEmployee=()=>{
        
    }

    const onAdd=()=>{
        let config = {
            headers: {"Access-Control-Allow-Origin":true,
          }
        }
          axios.post(`${API}/addemployee`,{
            fname:"Naor",
            lname:"Levi",
            phone:"0500000000",
            address:"rishon,goliath 52",
            roll:"HR"
          },config)
          .then(function (response) {
            console.log(response);
          })
          .catch(function (error) {
            alert('Something went Wrong! Try Again')
          });
    }
    
    const renderEmployee=({item})=>{
        var  months = ["Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec"];
        var d = new Date(item.datejoined);
        var monthName=months[d.getMonth()];
        var dayName=d.getDate();
        var yearName=d.getFullYear();
        return(
            <View style={styles.item}>
                <Image style={styles.image}
                source={{uri:'https://picsum.photos/150'}}
                resizeMode='contain'
                />
                <View style={{marginLeft:10,flexDirection:'row'}}>
                <Text style={{width:185,fontWeight:'600',fontFamily:'sans-serif',fontSize:15}}>{item.fname}</Text>
                <Text style={{width:185,fontWeight:'600',fontFamily:'sans-serif',fontSize:15}}>{item.lname}</Text>
                <Text style={{width:185,fontWeight:'600',fontFamily:'sans-serif',fontSize:15}}>{`${item.phone.slice(0,3)} ${item.phone.slice(3,10)}`}</Text>
                <Text style={{width:220,fontWeight:'600',fontFamily:'sans-serif',fontSize:15}}>{item.address}</Text>
                <Text style={{width:170,marginHorizontal:70,fontWeight:'600',fontFamily:'sans-serif',fontSize:15}}>{`${item.roll}`}</Text>
                <Text style={{fontWeight:'600',fontFamily:'sans-serif',fontSize:15}}>{`${dayName} ${monthName} ${yearName}`}</Text>
                {isEditing}
                </View>
            </View>
        )
    }
    return(
    <SafeAreaView style={{backgroundColor:'#edf1f2'}}>
      <View style={{flex: 1, position: 'relative',color:'black'}}>
      <StatusBar style="auto" />
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Managing Employees</Text>
      <View style={{left:0,bottom:30,alignSelf:'flex-end'}}>
      <CustomButton onPress={onAddEmployee} text={'+ Add Employee'}></CustomButton>
      </View>
      <View style={styles.whiteb}>
      <View style={{flexDirection:'row',marginLeft:100,marginTop:20}}>
         <Text style={[styles.tabletitle,{width:185}]}>First Name</Text>
          <Text style={[styles.tabletitle,{width:185}]}>Last Name</Text>
          <Text style={[styles.tabletitle,{width:185}]}>Phone</Text>
          <Text style={[styles.tabletitle,{width:220}]}>Address</Text>
          <Text style={[styles.tabletitle,{width:170,marginHorizontal:70}]}>Roll</Text>
          <Text style={[styles.tabletitle,{}]}>Start Date</Text>
          </View>
          {
              isLoading?<ActivityIndicator style={{alignSelf:'center'}}/>:(
      <FlatList style={{ backgroundColor: 'white'}} showsVerticalScrollIndicator={false} data={data}
      keyExtractor={item=>`key-${item.id}`}
      renderItem={renderEmployee}>
      </FlatList>
      )
     }
      </View>
      </View>
      </View>
    </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        resizeMode: 'contain',
        alignSelf:'center',
        width:1300,
        backgroundColor:'#edf1f2'
    },
    sectionTitle:{
        alignSelf:'flex-start',
        marginTop:'5%',
        fontSize: 30,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    whiteb:{
        marginTop:45,
        backgroundColor:'white',
        height:700,
        width:'100%',
    },
    tabletitle:{
        color:'lightslategrey',
        fontSize:13,
        marginBottom:15,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    item:{
        flexDirection:'row',
        borderBottomColor:'#edf1f2',
        borderTopColor:'#edf1f2',
        borderTopWidth:1.5,
        borderBottomWidth:1.5,
        width:'100%',
        height:85,
        alignItems:'center'
    },
    image:{
        width:65,
        height:65,
        borderRadius:80,
        marginLeft:25,
    },
})
