import i18n, { init } from "i18next";
import { useTranslation, initReactI18next } from "react-i18next";
import En from "./En.json"
import He from "./He.json"
const resources={
    en:{
        translation:{
            "Sign In" : "Sign shot" ,
            "Sign Up" : "Sign Up" ,
            "En" : "En",
            "He" : "He"
        },
    he:{
        translation:{
            "Sign In" : "התחבר" ,
            "Sign Up" : "הירשם",
            "En" : "אנ",
            "He" : "עב"
        }
    }
    }
}

i18n
  .use(initReactI18next) // passes i18n down to react-i18next
  .init({
    resources,
    lng:'en',
    keySeparator:false,
    fallbackLng:'en',
    interpolation: {
      escapeValue: false
    }
  });

  export default i18n;
