import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView} from 'react-native';
import {LangPicker} from '../components/LangPicker';
import CustomInput from '../components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from '../components/CustomButton';
import react from 'react';
import { useNavigation ,useRoute} from '@react-navigation/native';


const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function AppHome() {

    const navigation=useNavigation();

    const { isAdding, isUpdating } = route.params;
    const [disable, setDisable]=useState(false);

    const onAdd=()=>{
        if(!disable){
        setDisable(true);
        if(isValidForm()){
          axios.post(`${API}/addemployee`,{
            fname:Fname,
            lname:Lname,
            phone:Phone,
            address:Address,
            roll:Roll
          },config)
          .then(function (response) {
            console.log(response);
            setAdding(false);
            setDisable(false);
            navigation.replace('WebHome');
          })
          .catch(function (error) {
            if(currentlanguage==='En'){
            alert('Something went Wrong! Try Again')
            }
            else{
            alert('משהו קרה נסה שוב!')
            }
            setDisable(false);
          });
        }
        else{
            setDisable(false);
            return;
        }
        }
    }

    const updateItem=()=>{
        if(!disable){
        setDisable(true);
        if(isValidForm()){
          axios.post(`${API}/update`,{
            id:Id,
            fname:Fname,
            lname:Lname,
            phone:Phone,
            address:Address,
            roll:Roll
          },config)
          .then(function (response) {
            console.log(response);
            setUpdating(false);
            setDisable(false);
            navigation.replace('WebHome');
          })
          .catch(function (error) {
            if(currentlanguage==='En'){
              alert('Something went Wrong! Try Again')
              }
              else{
              alert('משהו קרה נסה שוב!')
              }
            setDisable(false);
          });
        }
        else{
            setDisable(false);
            return;
        }
        }
    }

    return(
    <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>
      <StatusBar style="auto" />
      <View style={{width:'90%',alignSelf:'center'}}>
      <Icon onPress={()=>navigation.replace('SignIn')} name="chevron-small-left" size={50} style={{color:'lightslategrey',marginTop:15}}/>
      <Text style={styles.sectionTitle}>{t('Add Employees')}</Text>
      <CustomInput value={Fname} setValue={setFname} direct={'left'} placeholder={t('First Name')} />
      <CustomInput value={Lname} setValue={setLname} direct={'left'} placeholder={t('Last Name')} />
      <CustomInput value={Phone} setValue={setPhone} direct={'left'} placeholder={t('Phone')} />
      <CustomInput value={Address} setValue={setAddress} direct={'left'} placeholder={t('Address')} />
      <CustomInput value={Roll} setValue={setRoll} direct={'left'} placeholder={t('Roll')} />
      {isAdding?<CustomButton disable={disable} onPress={onAdd} text={t('Add')}></CustomButton>:
      <CustomButton disable={disable} onPress={updateItem} text={t('Update')}></CustomButton>}
      </View>
    </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        resizeMode: 'contain',
        flex:1,
    },
    sectionTitle:{
        alignSelf:'flex-start',
        marginTop:50,
        fontSize: 20,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    whiteb:{
        marginTop:50,
        backgroundColor:'white',
        height:500,
        width:'100%',
    },
})
