import react from "react";
import{
    StyleSheet,Text,View,
    TouchableOpacity, ScrollView,
    Dimensions , Platform
} from 'react-native'
const OPTIONS = ['En','He'];
const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

const LangPicker=(props)=>{
    const onPressItem=(option)=>{
        props.changeLangVisibility(false);
        props.setData(option);
    }
    const option=OPTIONS.map((item,index)=>{
        return(
            <TouchableOpacity
            style={styles.option}
            key={index}
            onPress={()=> onPressItem(item)}
            >
                <Text style={styles.text}>{item}
                </Text>
            </TouchableOpacity>
        )
    })
    return(
        <TouchableOpacity
        onPress={()=>props.changeLangVisibility(false)}
        style={styles.container}
        >
            <View style={styles.lang,{width:WIDTH-40,height:HEIGHT/2}}>
                <ScrollView>
                    {option}
                </ScrollView>
            </View>

        </TouchableOpacity>
    )
}
const styles= StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'flex-end',
        },
    lang:{
        backgroundColor: 'white',
        borderRadius: 10
        },
    option:{
        alignItems:'center',
    },
    text:{
        margin:5,
        fontSize:22,
        fontWeight:'normal',
        ...Platform.select({
            ios: {
            color:'grey',
            },
            android: {
            color:'grey',
            },
            default: {
              color:'black',
              
            },
          })
    },
})

export {LangPicker}
