import { StatusBar } from 'expo-status-bar';
import { useEffect, useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView, FlatList, ViewBase, ActivityIndicator} from 'react-native';
import CustomButton from '../components/CustomButton';
import react from 'react';
import axios from 'axios';
import { API } from '../config';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function WebHome() {
    const [data,setData]=useState([]);
    const [isLoading,setLoading]=useState(false);

    const [completeScrollBarHeight, setCompleteScrollBarHeight] = useState(1);
    const [visibleScrollBarHeight, setVisibleScrollBarHeight] = useState(0);

    const scrollIndicatorSize =
    completeScrollBarHeight > visibleScrollBarHeight
    ? (visibleScrollBarHeight * visibleScrollBarHeight) /
      completeScrollBarHeight
    : visibleScrollBarHeight;
    useEffect(()=>{
        setLoading(true);
        axios.get(`${API}/employees`,{headers:{"Access-Control-Allow-Origin":true}})
        .then(({data})=>setData(data))
        .catch(console.error)
        .finally(()=>setLoading(false));
    },[])
    const onAddEmployee=()=>{
        let config = {
            headers: {"Access-Control-Allow-Origin":true,
          }
        }
          axios.post(`${API}/addemployee`,{
            fname:"Naor",
            lname:"Levi",
            phone:"0500000000",
            address:"rishon,goliath 52",
            roll:"HR"
          },config)
          .then(function (response) {
            console.log(response);
          })
          .catch(function (error) {
            alert('Something went Wrong! Try Again')
          });
    }
    const renderEmployee=({item})=>{
        var  months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        var d = new Date(item.datejoined);
        var monthName=months[d.getMonth()];
        var dayName=d.getDate();
        var yearName=d.getFullYear();
        return(
            <View style={styles.item}>
                <Image style={styles.image}
                source={{uri:'https://picsum.photos/150'}}
                resizeMode='contain'
                />
                <View style={{marginLeft:15,justifyContent:'center'}}>
                <Text styles={styles.sectionTitle}>{`${item.fname}                                   ${item.lname}                                       ${item.phone.slice(0,3)} ${item.phone.slice(3,10)}                   ${item.address}                              ${item.roll}                                     ${dayName} ${monthName} ${yearName}`}</Text>
                </View>
            </View>
        )
    }
    return(
    <SafeAreaView style={{backgroundColor:'#edf1f2'}}>
      <StatusBar style="auto" />
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Managing Employees</Text>
      <View style={{left:0,bottom:30,alignSelf:'flex-end'}}>
      <CustomButton onPress={onAddEmployee} text={'+ Add Employee'}></CustomButton>
      </View>
      <View style={styles.whiteb}>
      <View style={{flexDirection:'row',marginLeft:20,marginTop:20,justifyContent:'space-around'}}>
         <Text style={styles.tabletitle}>First Name</Text>
          <Text style={styles.tabletitle}>Last Name</Text>
          <Text style={styles.tabletitle}>Phone</Text>
          <Text style={styles.tabletitle}>Address</Text>
          <Text style={styles.tabletitle}>             Roll          </Text>
          <Text style={styles.tabletitle}>Start Date</Text>
          </View>
          {
              isLoading?<ActivityIndicator style={{alignSelf:'center'}}/>:(
              <View style={{ flex: 1, flexDirection: 'row'}}>
      <FlatList style={{ backgroundColor: 'white'}} showsVerticalScrollIndicator={false} data={data}
      keyExtractor={item=>`key-${item.id}`}
      renderItem={renderEmployee}>
           <View style={{height: '100%',width: 6,backgroundColor: '#52057b',borderRadius: 8}}></View>
      </FlatList>
      <View
      style={{
      height: '100%',
      width: 6,
      backgroundColor: '#52057b',
      borderRadius: 8
    }}>
    <View
      style={{
        width: 6,
        borderRadius: 8,
        backgroundColor: '#bc6ff1',
        height: scrollIndicatorSize
      }}
    />
  </View>
</View>
      )
     }
      </View>
      </View>
    </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        resizeMode: 'contain',
        alignSelf:'center',
        width:1300,
        backgroundColor:'#edf1f2'
    },
    sectionTitle:{
        alignSelf:'flex-start',
        marginTop:'5%',
        fontSize: 30,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    whiteb:{
        marginTop:45,
        backgroundColor:'white',
        height:700,
        width:'100%',
    },
    tabletitle:{
        color:'lightslategrey',
        fontSize:13,
        marginBottom:15,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    item:{
        flexDirection:'row',
        borderBottomColor:'#edf1f2',
        borderTopColor:'#edf1f2',
        borderTopWidth:1.5,
        borderBottomWidth:1.5,
        width:'100%',
        height:85,
        alignItems:'center'
    },
    image:{
        width:65,
        height:65,
        borderRadius:80,
        marginLeft:25,
    },
})
