import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal
,Platform,Image,Dimensions, ImageBackground,ScrollView} from 'react-native';
import {LangPicker} from './components/LangPicker';
import CustomInput from './components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from './components/CustomButton';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function App() {

  const[username,setUsername]=useState('');
  const[password,setPassword]=useState('');

  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }

  const onSignIn=()=>{
    console.warn('Sign In');
  };

  return (
    <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={[styles.text,{}]}>{chooseData}</Text><Icon name="chevron-down" size={19} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    <ImageBackground source={require('./assets/profilepic.png')} style={{ width: 130, height: 130,
    alignSelf:'center',position: 'absolute',marginTop:115,zIndex:1, ...Platform.select({
      ios:{marginTop:200},
      android:{},
      default:{},
    })}}>
    <Image source={require('./assets/bluePlus.jpg')} style={{ width: 32, height: 32,marginLeft:96,marginTop:100}}></Image>
    </ImageBackground>
    <View style={styles.whiteb}>
      <View style={{flex:1.,justifyContent:'center',alignItems:'center'}}>
      <Text style={{marginLeft:5,width:'79%',color:'dodgerblue',fontWeight:'bold',fontSize:14}}>Email</Text>
      <CustomInput value={username} setValue={setUsername} placeholder='Username' />
      <Text style={{marginLeft:5,width:'79%',color:'dodgerblue',fontWeight:'bold',fontSize:14,marginTop:25}}>Password</Text>
      <CustomInput value={password} setValue={setPassword} placeholder='Password' hidden='true'/>
      <CustomButton text={'Sign In'} onPress={onSignIn}></CustomButton>
      </View>
      <Text style={styles.bluetext}>Forgot password?</Text>
    </View>
    </View>
    <View style={{flexDirection:'row',justifyContent:'center',bottom:'8%'}}>
      <Text style={{fontSize:18,fontWeight:'500',fontFamily:'sans-serif'}}>Don't have an account? </Text>
      <Text style={{fontSize:18,color:'dodgerblue',fontWeight:'500',fontFamily:'sans-serif'}}>Sign Up</Text>
    </View>
    <Text style={{width:'100%',textAlign:'center',bottom:-100,textDecorationLine: 'underline',fontWeight:'500',fontFamily:'sans-serif'}}>Our Terms of Use and Privacy Policy</Text>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    resizeMode: 'contain',
    flex:1,
    ...Platform.select({
      ios:{
      },
      android:{
      },
      default:{
        marginTop:HEIGHT/10,
        width:WIDTH/3+50,
        alignSelf:'center',
      },
    })
  },
  sectionTitle: {
    height: 50,
    fontSize: 34,
    fontWeight: 'bold',
    fontFamily:'sans-serif',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 40,
    marginTop: 80,
    },
    default: {
      alignSelf:'center',
    },
  })
  },
  icon:{
    alignItems:'center',
    marginTop:2,
      ...Platform.select({
      ios: {
        color:'grey',
        fontSize:25,
            },
      android: {
        color:'grey'
            },
      default: {
        color:'black',
        marginLeft:16,
      },
    })
      },
  text:{
    fontSize:14,
    fontWeight:'500',
    ...Platform.select({
      ios: {
      fontSize:18,
      color:'grey',
      },
      android: {
      fontSize:18,
      color:'grey',
      },
      default: {
        color:'black',
        marginLeft:10,
      },
    })
  },
  square:{
    width: 70,
    height: 40,
    flexDirection:'row',
    alignItems:'center',
    ...Platform.select({
      ios: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      android: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      default: {
        marginTop:80  ,
        backgroundColor:'white',
        borderColor: "#e2e1df",
        borderWidth:1.6,
        opacity:0.9
      },
    })
    },
  touchableOpacity:{
    ...Platform.select({
      ios: {
        position:'absolute',
        alignSelf:'flex-end',
        top:'2%',
        right:'2%',
            },
      android: {
        position:'absolute',
        alignSelf:'flex-end',
        top:'2%',
        right:'2%',
            },
      default: {
        alignSelf:'flex-end',
      },
    })
    },
    whiteb:{
      backgroundColor: 'white',
      ...Platform.select({
        ios: {
          marginTop:100,
          flex:1,

          borderRadius:15
              },
        android: {
          marginTop:100,
          flex:1,
          borderRadius:15
              },
        default: {
          marginTop:20,
          flex:0.8,
          borderColor: "#e2e1df",
          borderWidth:1,
        },
      }) 
    },
    bluetext:{
      textDecorationLine: 'underline',
      color:'dodgerblue',
      ...Platform.select({
        ios:{position:'absolute',width:'52%',bottom:'34%',textAlign:'center'},
        android:{position:'absolute',width:'52%',bottom:'34%',textAlign:'center'},
        default:{position:'absolute',bottom:'5%',alignSelf:'center',fontWeight:'500'},
      }),
    }
});
