import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Touchable, TouchableOpacity, View,Modal , Platform} from 'react-native';
import {LangPicker} from './components/LangPicker';
import {DownOutlined } from '@ant-design/icons'
export default function App() {


  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }
  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.container}> 
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      </View>
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity,
        {alignItems:'center', justifyContent:'center',}}>
        <Text style={styles.text}>{chooseData}</Text>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#edf1f2',
    resizeMode: 'contain', 
  },
  sectionTitle: {
    height: 50,
    fontSize: 40,
    fontWeight: 'bold',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 20,
      marginTop: 30,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 20,
    marginTop: 30,
    },
    default: {
      alignSelf:'center'
    },
  })
  },
  text:{
    fontSize:22,
    ...Platform.select({
      ios: {
      color:'grey',
      },
      android: {
      color:'grey',
      },
      default: {
        color:'black',

      },
    })
  },
  touchableOpacity:{
    backgroundColor: '#edf1f2',
    alignSelf:'auto',
    },
});
