import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Touchable, TouchableOpacity, View,lang } from 'react-native';
import {langPicker} from './components/langPicker'

export default function App() {
  const [chooseData,setchooseData]=useState('EN');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }
  return (
    <SafeAreaView style={container}>
    <TouchableOpacity
    onPress={()=>changeLangVisibility(true)} 
    style={styles.touchableOpacity}>
    <Text style={styles.text}>{chooseData}</Text>
    </TouchableOpacity>
    <lang
      transparent={true}
      animationType='fade'
      visible= {isLangVisible}
      nRequestClose={()=>changeLangVisibility(false)}
      >
        <langPicker>
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        </langPicker>
    </lang>
    <View style={styles.container}> 
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      </View>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#edf1f2',
    resizeMode: 'contain', 
  },
  sectionTitle: {
    fontSize: 40,
    fontWeight: 'bold'
  },
  text:{
    marginVertical: 20,
    fontSize:25
  },
  touchableOpacity:{
    backgroundColor: 'orange',
    alignSelf: 'stretch',
    paddingHorizontal:20
  },
});
