import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Touchable, TouchableOpacity, View,Modal , Platform,Image,Dimensions} from 'react-native';
import {LangPicker} from './components/LangPicker';
import Icon from 'react-native-vector-icons/Entypo';
const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;
export default function App() {

  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }
  return (
    <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={styles.text}>{chooseData}</Text><Icon name="chevron-down" size={24} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    <View style={styles.whiteb}>
    <Image source={require('./assets/profilepic.png')} style={{ width: 140, height: 140,alignSelf:'center'}}></Image>
    </View>
    </View>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    resizeMode: 'contain',
    flex:1,
    ...Platform.select({
      ios:{
      },
      android:{
      },
      default:{
        marginTop:HEIGHT/10,
        width:WIDTH/3+50,
        alignSelf:'center',
      },
    })
  },
  sectionTitle: {
    height: 50,
    fontSize: 34,
    fontWeight: 'bold',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 40,
    marginTop: 80,
    },
    default: {
      alignSelf:'center',
    },
  })
  },
  icon:{
    alignItems:'center',
    marginTop:4,
    marginLeft:6,
      ...Platform.select({
      ios: {
        color:'grey'
            },
      android: {
        color:'grey'
            },
      default: {
        color:'black',
        marginLeft:10,
      },
    })
      },
  text:{
    fontSize:20,
    marginLeft:10,
    ...Platform.select({
      ios: {
      color:'grey',
      },
      android: {
      color:'grey',
      },
      default: {
        color:'black',

      },
    })
  },
  square:{
    width: 70,
    height: 40,
    flexDirection:'row',
    alignItems:'center',
    ...Platform.select({
      ios: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      android: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      default: {
        marginTop:80  ,
        backgroundColor:'white',
        borderColor: "#e2e1df",
        borderWidth:1.6,
        opacity:0.9
      },
    })
    },
  touchableOpacity:{
    ...Platform.select({
      ios: {
        alignSelf:'flex-end',
            },
      android: {
        alignSelf:'flex-end',        
            },
      default: {
        alignSelf:'flex-end',
      },
    })
    },
    whiteb:{
      backgroundColor: 'white',
      ...Platform.select({
        ios: {
          marginTop:100,
          flex:1,
          marginBottom:20,
          borderRadius:15
              },
        android: {
          marginTop:100,
          flex:1,
          marginBottom:20,
          borderRadius:15
              },
        default: {
          marginTop:20,
          height:HEIGHT/2,
          borderColor: "#e2e1df",
          borderWidth:1,
        },
      }) 
    }
});
