import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Touchable, TouchableOpacity, View,Modal , Platform,Image,Dimensions, ImageBackground,TextInput, ViewBase} from 'react-native';
import {LangPicker} from './components/LangPicker';
import Icon from 'react-native-vector-icons/Entypo';
import CustomInput from './components/CustomInput';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function App() {

  const[username,setUsername]=useState('');
  const[password,setPassword]=useState('');

  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }
  return (
    <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={[styles.text,{marginLeft:10,}]}>{chooseData}</Text><Icon name="chevron-down" size={19} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    <ImageBackground source={require('./assets/profilepic.png')} style={{ width: 130, height: 130,
    alignSelf:'center',position: 'absolute',marginTop:115,zIndex:1, ...Platform.select({
      ios:{marginTop:200},
      android:{},
      default:{},
    })}}>
    <Image source={require('./assets/bluePlus.jpg')} style={{ width: 32, height: 32,marginLeft:96,marginTop:100}}></Image>
    </ImageBackground>
    <View style={styles.whiteb}>
      <View style={{flex:1,justifyContent:'center',alignItems:'center'}}>
      <Text style={{width:'76%',color:'dodgerblue',fontWeight:'bold',fontSize:15}}>Email</Text>
      <TextInput ></TextInput>
       <Text style={{width:'76%',color:'dodgerblue',fontWeight:'bold',fontSize:15,marginTop:25}}>Password</Text>
      <TextInput value={password} setValue={setPassword} onChangeText={setValue} secureTextEntry={true} style={[styles.text,{width:'78%',lineHeight: Platform.OS === 'ios' ? 20 : 40,height:Platform.OS === 'ios' ? 25 : 42
       ,borderBottomWidth:2,borderBottomColor:'lightsteelblue'}]} placeholder='Enter password'></TextInput>
      </View>
    </View>
    </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    resizeMode: 'contain',
    flex:1,
    ...Platform.select({
      ios:{
      },
      android:{
      },
      default:{
        marginTop:HEIGHT/10,
        width:WIDTH/3+50,
        alignSelf:'center',
      },
    })
  },
  sectionTitle: {
    height: 50,
    fontSize: 34,
    fontWeight: 'bold',
    fontFamily:'sans-serif',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 40,
    marginTop: 80,
    },
    default: {
      alignSelf:'center',
    },
  })
  },
  icon:{
    alignItems:'center',
    marginTop:2,
    marginLeft:15,
      ...Platform.select({
      ios: {
        color:'grey'
            },
      android: {
        color:'grey'
            },
      default: {
        color:'black',
        marginLeft:16,
      },
    })
      },
  text:{
    fontSize:14,
    fontWeight:'500',
    ...Platform.select({
      ios: {
      color:'grey',
      },
      android: {
      color:'grey',
      },
      default: {
        color:'black',

      },
    })
  },
  square:{
    width: 70,
    height: 40,
    flexDirection:'row',
    alignItems:'center',
    ...Platform.select({
      ios: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      android: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      default: {
        marginTop:80  ,
        backgroundColor:'white',
        borderColor: "#e2e1df",
        borderWidth:1.6,
        opacity:0.9
      },
    })
    },
  touchableOpacity:{
    ...Platform.select({
      ios: {
        alignSelf:'flex-end',
            },
      android: {
        alignSelf:'flex-end',        
            },
      default: {
        alignSelf:'flex-end',
      },
    })
    },
    whiteb:{
      backgroundColor: 'white',
      ...Platform.select({
        ios: {
          marginTop:100,
          flex:1,
          marginBottom:20,
          borderRadius:15
              },
        android: {
          marginTop:100,
          flex:1,
          marginBottom:20,
          borderRadius:15
              },
        default: {
          marginTop:20,
          flex:0.8,
          borderColor: "#e2e1df",
          borderWidth:1,
        },
      }) 
    },
});
