import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, Touchable, TouchableOpacity, View,Modal , Platform,Image,Dimensions} from 'react-native';
import {LangPicker} from './components/LangPicker';
import Icon from 'react-native-vector-icons/Entypo';
const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;
export default function App() {

  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }
  return (
    <SafeAreaView style={styles.container}>
      <Text style={styles.sectionTitle}>Sign In</Text>
      <StatusBar style="auto" />
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={styles.text}>{chooseData}</Text><Icon name="chevron-down" size={24} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    </SafeAreaView>

  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#edf1f2',
    resizeMode: 'contain', 
    flex:1,
    marginTop:HEIGHT/5,
  },
  sectionTitle: {
    height: 50,
    fontSize: 35,
    fontWeight: 'bold',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 40,
    marginTop: 80,
    },
    default: {
      alignSelf:'center',
      marginTop:130
    },
  })
  },
  icon:{
    alignItems:'center',
    marginTop:4,
    marginLeft:15
      },
  text:{
    fontSize:22,
    marginLeft:10,
    ...Platform.select({
      ios: {
      color:'grey',
      },
      android: {
      color:'grey',
      },
      default: {
        color:'black',

      },
    })
  },
  square:{
    marginTop:120,
    width: 80,
    height: 50,
    flexDirection:'row',
    alignItems:'center',
    ...Platform.select({
      ios: {
        backgroundColor:'#edf1f2'
            },
      android: {
        backgroundColor:'#edf1f2'
            },
      default: {
        backgroundColor:'white',
        borderColor: "#e2e1df",
        borderWidth: 1.6,
        opacity:0.9
      },
    })
    },
  touchableOpacity:{
    ...Platform.select({
      ios: {
        alignSelf:'flex-end',
        width:50        
      },
      android: {
        alignSelf:'flex-end',        
            },
      default: {
        alignSelf:'flex-end',
        marginRight:400,
      },
    })
    },
});
