import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView} from 'react-native';
import {LangPicker} from './components/LangPicker';
import CustomInput from './components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from './components/CustomButton';
import SignUp from './SignUp';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

const Stack = createNativeStackNavigator();


export default function App(navigation) {

  const[username,setUsername]=useState('');
  const[password,setPassword]=useState('');

  const [chooseData,setchooseData]=useState('En');
  const [isLangVisible,setisLangVisible]= useState(false);

  const changeLangVisibility=(bool)=>{
    setisLangVisible(bool)
  }
  const setData=(option)=>{
    setchooseData(option)
  }

  const onSignIn=()=>{
    console.warn('Sign In');
  };

  return (
    <NavigationContainer>{
      <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>

      <StatusBar style="auto" />
      <ScrollView>
      <KeyboardAvoidingView behavior='' style={{flex:1}}>
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Sign In</Text>
      <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
      style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={[styles.text]}>{chooseData}</Text><Icon name="chevron-down" size={19} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    <ImageBackground source={require('./assets/profilepic.png')} style={{ width: 130, height: 130,
    alignSelf:'center',position: 'absolute',marginTop:115,zIndex:1, ...Platform.select({
      ios:{marginTop:150},
      android:{marginTop:150},
      default:{},
    })}}>
    <Image source={require('./assets/bluePlus.jpg')} style={{ width: 32, height: 32,marginLeft:96,marginTop:100}}></Image>
    </ImageBackground>
    <View style={styles.whiteb}>
      <View style={styles.inputContainer}>
      <Text style={{marginLeft:5,width:'79%',color:'dodgerblue',fontWeight:'bold',fontSize:14}}>Email</Text>
      <CustomInput value={username} setValue={setUsername} placeholder='Username' />
      <Text style={{marginLeft:5,width:'79%',color:'dodgerblue',fontWeight:'bold',fontSize:14,marginTop:25}}>Password</Text>
      <CustomInput value={password} setValue={setPassword} placeholder='Password' hidden='true'/>
      <CustomButton text={'Sign In'} onPress={onSignIn}></CustomButton>
      </View>
      <Text style={styles.bluetext}>Forgot password?</Text>
    </View>
    </View>
    <View style={{flexDirection:'row',justifyContent:'center',alignSelf:'center',
    ...Platform.select({
      ios:{position:'absolute',bottom:'30%'},
      android:{position:'absolute',bottom:'20%'},
      default:{marginTop:12},
    })
    }}> 
      <Text style={{fontSize:18,fontWeight:'500',fontFamily:'sans-serif'}}>Don't have an account? </Text>
      <Text onPress={() => navigation.navigate('SignUp')} style={{fontSize:18,color:'dodgerblue',fontWeight:'500',fontFamily:'sans-serif'}}>Sign Up</Text>
    </View>
    <Text style={{alignSelf:'center',textAlign:'center',textDecorationLine: 'underline',fontWeight:'500',fontFamily:'sans-serif',
    ...Platform.select({
      ios:{position:'absolute',bottom:'0%'},
      android:{position:'absolute',bottom:'0%'},
      default:{marginTop:150},   
    })
    }}>Our Terms of Use and Privacy Policy</Text>
    </KeyboardAvoidingView>
    </ScrollView>
    </SafeAreaView>
    }</NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    resizeMode: 'contain',
    flex:1,
    ...Platform.select({
      ios:{
      },
      android:{
      },
      default:{
        marginTop:HEIGHT/10,
        width:WIDTH/3+50,
        alignSelf:'center',
      },
    })
  },
  inputContainer:{
    flex:1,
    justifyContent:'center',
    alignItems:'center',
    ...Platform.select({
      ios:{
        marginTop:'20%',
      },
      android:{
        marginTop:'15%',
      },
      default:{
      },
    })
  },
  sectionTitle: {
    height: 50,
    fontSize: 34,
    fontWeight: 'bold',
    fontFamily:'sans-serif',
    ...Platform.select({
    ios: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
    },
    android: {
    alignSelf:'flex-start',
    marginLeft: 40,
    marginTop: 80,
    },
    default: {
      alignSelf:'center',
    },
  })
  },
  icon:{
    alignItems:'center',
    marginTop:2,
      ...Platform.select({
      ios: {
        color:'grey',
        fontSize:25,
        marginLeft:5,
            },
      android: {
        color:'grey',
        fontSize:25,
        marginLeft:5,
            },
      default: {
        color:'black',
        marginLeft:16,
      },
    })
      },
  text:{
    fontSize:14,
    fontWeight:'500',
    ...Platform.select({
      ios: {
      fontSize:18,
      color:'grey',
      },
      android: {
      fontSize:18,
      color:'grey',
      },
      default: {
        color:'black',
        marginLeft:10,
      },
    })
  },
  square:{
    width: 70,
    height: 40,
    flexDirection:'row',
    alignItems:'center',
    marginTop:20,
    right:'8%',
    ...Platform.select({
      ios: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      android: {
        backgroundColor:'#edf1f2',
        opacity:0.5,
            },
      default: {
        marginTop:80  ,
        backgroundColor:'white',
        borderColor: "#e2e1df",
        borderWidth:1.6,
        opacity:0.9
      },
    })
    },
  touchableOpacity:{
    ...Platform.select({
      ios: {
        position:'absolute',
        alignSelf:'flex-end',
        top:'2%',
        right:'2%',
            },
      android: {
        position:'absolute',
        alignSelf:'flex-end',
        top:'2%',
        right:'2%',
            },
      default: {
        alignSelf:'flex-end',
      },
    })
    },
    whiteb:{
      backgroundColor: 'white',
      ...Platform.select({
        ios: {
          marginTop:100,
          flex:1,
          borderRadius:15,
          paddingBottom:'85%'
              },
        android: {
          marginTop:'20%',
          flex:1,
          borderRadius:15,
          paddingBottom:'70%',
              },
        default: {
          marginTop:20,
          flex:1,
          paddingVertical:55,
          borderColor: "#e2e1df",
          borderWidth:1,
        },
      }) 
    },
    bluetext:{
      textDecorationLine: 'underline',
      color:'dodgerblue',
      ...Platform.select({
        ios:{width:'52%',textAlign:'center',bottom:35},
        android:{width:'52%',marginTop:-35,textAlign:'center'},
        default:{position:'absolute',bottom:'5%',alignSelf:'center',fontWeight:'500'},
      }),
    }
});
