import react from 'react'
import{View,Text,TextInput,StyleSheet,Platform,TouchableOpacity, SafeAreaView} from 'react-native'
import Icon from 'react-native-vector-icons/MaterialIcons'
import { useState } from 'react';
const CustomInput=({value,setValue,placeholder,hidden})=>{

    const[show,setShow]=useState(false);
    const[visible,setVisible]=useState(false);
    const[hidePassword,setHidePassword]=useState(hidden);
    return(
        <SafeAreaView style={styles.container}>
            <TextInput
            placeholder={placeholder} 
            style={styles.input}
            value={value}
            onChangeText={setValue}
            secureTextEntry={visible}
            />
            {hidden ? <TouchableOpacity styles={styles.btnEye}
                onPress={()=>{
                setVisible(!visible)
                setShow(!show)
                }
            }>
                <Icon name={show===false?'visibility':'visibility-off'} size={21} style={styles.icon}></Icon>
                </TouchableOpacity> : null}
        </SafeAreaView>
    )
}
const styles=StyleSheet.create({
    container:{
        width:'80%',
    },
    input:{
        fontSize:14,
        fontWeight:'500',
        marginBottom:30,
        lineHeight: Platform.OS === 'ios' && 'android' ? 20 : 40,
        height:Platform.OS === 'ios' && 'android' ? 25 : 42,
        paddingHorizontal:5,
        borderBottomWidth:1.6,
        borderBottomColor:'#B0B8BF'
    },
    btnEye:{
        color:'black',
        paddingRight:'100%',
        alignItems:'flex-end',
    },
    icon:{
        position:'absolute',
        left:'95%',
        bottom:7
        },
})
export default CustomInput
