import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView} from 'react-native';
import {LangPicker} from '../components/LangPicker';
import CustomInput from '../components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from '../components/CustomButton';
import SignIn from './SignIn';
import react from 'react';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function WebHome() {
    return(
    <SafeAreaView style={{backgroundColor: '#edf1f2'}}>
      <StatusBar style="auto" />
      <View style={styles.container}>
      <Text style={styles.sectionTitle}>Managing Employees</Text>
      <View style={{left:0,bottom:30,alignSelf:'flex-end'}}>
      <CustomButton text={'+ Add Employee'}></CustomButton>
      </View>
      <View style={styles.whiteb}>
      <View style={{flexDirection:'row',alignSelf:'center',justifyContent:'space-around'}}>
         <Text>First Name</Text>
          <Text>Last Name</Text>
          <Text>Phone</Text>
          <Text>Address</Text>
          <Text>Roll</Text>
          <Text>Start Date</Text>
          </View>
      <ScrollView>
      </ScrollView>
      </View>
      </View>
    </SafeAreaView>
    )
}

const styles = StyleSheet.create({
    container:{
        alignSelf:'center',
        width:'82%',
    },
    sectionTitle:{
        alignSelf:'flex-start',
        marginTop:'5%',
        fontSize: 30,
        fontWeight: 'bold',
        fontFamily:'sans-serif',
    },
    whiteb:{
        marginTop:50,
        backgroundColor:'white',
        height:450,
        width:'100%',
    },
})
