import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { SafeAreaView, StyleSheet, Text, TouchableOpacity, View,Modal,Platform,Image,
Dimensions, ImageBackground,ScrollView,KeyboardAvoidingView} from 'react-native';
import {LangPicker} from '../components/LangPicker';
import CustomInput from '../components/CustomInput';
import Icon from 'react-native-vector-icons/Entypo';
import CustomButton from '../components/CustomButton';
import SignIn from './SignIn';
import react from 'react';

const WIDTH=Dimensions.get('window').width;
const HEIGHT=Dimensions.get('window').height;

export default function SignUp() {

    const[username,setUsername]=useState('');
    const[password,setPassword]=useState('');
  
    const [chooseData,setchooseData]=useState('En');
    const [isLangVisible,setisLangVisible]= useState(false);
  
    const changeLangVisibility=(bool)=>{
      setisLangVisible(bool)
    }
    const setData=(option)=>{
      setchooseData(option)
    }
  
    const onSignIn=()=>{
      console.warn('Sign In');
    };
    const onSignUp=()=>{
        console.warn('Sign Up');
      };
  
    return (
        <SafeAreaView style={{flex:1,backgroundColor: '#edf1f2'}}>
        <StatusBar style="auto" />
        <ScrollView>
        <KeyboardAvoidingView behavior='' style={{flex:1}}>
        <View style={styles.container}>
        <Text style={styles.sectionTitle}>Sign Up</Text>
        <TouchableOpacity  onPress={()=>changeLangVisibility(true)} 
        style={styles.touchableOpacity}>
        <View style={styles.square}>
        <Text style={[styles.text]}>{chooseData}</Text><Icon name="chevron-down" size={19} color="black" style={styles.icon} />
        </View>
        </TouchableOpacity>
        <Modal transparent={true}
        animationType='fade'
        visible= {isLangVisible}
        nRequestClose={()=>changeLangVisibility(false)
        }
      >
        <LangPicker
          changeLangVisibility={changeLangVisibility}
          setData={setData}
        />
    </Modal>
    <ImageBackground source={require('../assets/profilepic.png')} style={{ width: 130, height: 130,
    alignSelf:'center',position: 'absolute',marginTop:125,zIndex:1, ...Platform.select({
      ios:{marginTop:150},
      android:{marginTop:150},
      default:{},
    })}}>
    <Image source={require('../assets/bluePlus.jpg')} style={{ width: 32, height: 32,marginLeft:96,marginTop:100}}></Image>
    </ImageBackground>
    <View style={styles.whiteb}>
    <Text style={{top:50,color:'black',fontWeight:'400',fontSize:19}}>Personal Details</Text>
    <View style={styles.inputContainer}>
      </View>
    </View>
    </View>
        </KeyboardAvoidingView>
        </ScrollView>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
      resizeMode: 'contain',
      flex:1,
      ...Platform.select({
        ios:{
        },
        android:{
        },
        default:{
          marginTop:HEIGHT/10,
          width:WIDTH/3+50,
          alignSelf:'center',
        },
      })
    },
    inputContainer:{
      ...Platform.select({
        ios:{
          marginTop:'20%',
        },
        android:{
          marginTop:'15%',
        },
        default:{
        },
      })
    },
    sectionTitle: {
      height: 50,
      fontSize: 34,
      fontWeight: 'bold',
      fontFamily:'sans-serif',
      ...Platform.select({
      ios: {
        alignSelf:'flex-start',
        marginLeft: 40,
        marginTop: 80,
      },
      android: {
      alignSelf:'flex-start',
      marginLeft: 40,
      marginTop: 80,
      },
      default: {
        alignSelf:'center',
      },
    })
    },
    text:{
      fontSize:14,
      fontWeight:'500',
      ...Platform.select({
        ios: {
        fontSize:18,
        color:'grey',
        },
        android: {
        fontSize:18,
        color:'grey',
        },
        default: {
          color:'black',
          marginLeft:10,
        },
      })
    },
    square:{
      width: 70,
      height: 40,
      flexDirection:'row',
      alignItems:'center',
      marginTop:20,
      right:'8%',
      ...Platform.select({
        ios: {
          backgroundColor:'#edf1f2',
          opacity:0.5,
              },
        android: {
          backgroundColor:'#edf1f2',
          opacity:0.5,
              },
        default: {
          marginTop:80  ,
          backgroundColor:'white',
          borderColor: "#e2e1df",
          borderWidth:1.6,
          opacity:0.9
        },
      })
      },
      icon:{
        alignItems:'center',
        marginTop:2,
          ...Platform.select({
          ios: {
            color:'grey',
            fontSize:25,
            marginLeft:5,
                },
          android: {
            color:'grey',
            fontSize:25,
            marginLeft:5,
                },
          default: {
            color:'black',
            marginLeft:16,
          },
        })
          },
    touchableOpacity:{
      ...Platform.select({
        ios: {
          position:'absolute',
          alignSelf:'flex-end',
          top:'2%',
          right:'2%',
              },
        android: {
          position:'absolute',
          alignSelf:'flex-end',
          top:'2%',
          right:'2%',
              },
        default: {
          alignSelf:'flex-end',
        },
      })
      },
      whiteb:{
        backgroundColor: 'white',
        ...Platform.select({
          ios: {
            marginTop:100,
            flex:1,
            borderRadius:15,
            paddingBottom:'85%'
                },
          android: {
            marginTop:'20%',
            flex:1,
            borderRadius:15,
            paddingBottom:'70%',
                },
          default: {
            marginTop:20,
            flex:1,
            paddingVertical:330,
            borderColor: "#e2e1df",
            borderWidth:1,
          },
        }) 
      },
  });
