import i18n, { init } from "i18next";
import { useTranslation, initReactI18next, Translation } from "react-i18next";
import En from "./En.json"
import He from "./He.json"

const resources={
    En:{translation:{
      He
    }},
    He:{translation:{
        En
    }},
};

i18n
  .use(initReactI18next) // passes i18n down to react-i18next
  .init({
    resources,
    lng: "En",
    fallbackLng: "En",
    interpolation: {
      escapeValue: false
    }
  });

  export default i18n;
